import React from 'react';
//import './Authorization.css';
import { toast, ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

const axios = require('axios');
const path = "http://localhost:1234/users";

export default class Authorization extends React.Component {
    state = {
        email: "",
        pwd: "",
        id: ""
    }

    notify(err) {
        toast.error(`${err}`, {
            position: "top-center",
            autoClose: 2000,
            hideProgressBar: true,
            closeOnClick: false,
            pauseOnHover: true,
            draggable: true
        });
    }

    readEmailInput(e) {
        const { value } = e.target;
        this.setState({ email: value });
    }

    readPwdInput(e) {
        const { value } = e.target;
        this.setState({ pwd: value });
    }

    compareUserData() {
        console.log("### ", this.state)
        this.props.changeStateApp();
    }

    render() {
        return (
            <div className="Authorization">
                <ToastContainer />
                <header className="Authorization-header">Authorization</header>
                <div className="creds">
                    <div className="email-block">
                        <p className="email-p">Email :</p>
                        <input
                            type="text"
                            className="email-value"
                            onKeyPress={(e) => this.readEmailInput(e)}
                        />
                    </div>
                    <div className="pwd-block">
                        <p className="pwd-p">Password :</p>
                        <input
                            type="password"
                            className="pwd-value"
                            onKeyPress={(e) => this.readPwdInput(e)}
                        />
                    </div>
                    <div className="but-block">
                        <input
                            type="submit"
                            value="Sign in"
                            className="login"
                            onClick={() => this.compareUserData()}
                        />
                        <input
                            type="submit"
                            className="register"
                            value="Register"
                            onClick={() => this.compareUserData()}
                        />
                    </div>
                </div>
            </div>
        )
    }
}
