import React from 'react';
//import './Authorization.css';
import { toast, ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

const axios = require('axios');
const path = "http://localhost:1234/users";

export default class Authorization extends React.Component {
    state = {
        email: "",
        pwd: "",
        id: ""
    }

    componentDidMount() {
        axios.get(`${path}`)
    }

    notify(err) {
        toast.error(`${err}`, {
            position: "top-center",
            autoClose: 2000,
            hideProgressBar: true,
            closeOnClick: false,
            pauseOnHover: true,
            draggable: true
        });
    }

    readEmailInput = (e) => {
        const { value } = e.target;
        this.setState({ email: value });
    }

    readPwdInput = (e) => {
        const { value } = e.target;
        this.setState({ pwd: value });
    }

    compareUserData() {
        // if (e.target.value === "Register") {
        let user = {
            email: this.state.email,
            pwd: this.state.pwd
        }
        axios.post(`${path}`, user)
            .then(() => {
                this.props.changeStateApp();
            })
            .catch(err => {
                this.notify(err);
            })
        // } else if (e.target.value === "Sign in") {
        //     axios.get("http://localhost:1234/users")
        //         .then(() => {
        //             this.props.changeStateApp();
        //         })
        //         .catch(err => {
        //             this.notify(err);
        //         })
        // }
    }

    render() {
        return (
            <div className="Authorization">
                <ToastContainer />
                <header className="Authorization-header">Authorization</header>
                <div className="creds">
                    <div className="email-block">
                        <p className="email-p">Email :</p>
                        <input
                            type="text"
                            className="email-value"
                            onChange={(e) => this.readEmailInput(e)}
                        />
                    </div>
                    <div className="pwd-block">
                        <p className="pwd-p">Password :</p>
                        <input
                            type="password"
                            className="pwd-value"
                            onChange={(e) => this.readPwdInput(e)}
                        />
                    </div>
                    <div className="but-block">
                        <input
                            type="submit"
                            value="Sign in"
                            className="login"
                            onClick={() => this.compareUserData()}
                        />
                        <input
                            type="submit"
                            className="register"
                            value="Register"
                            onClick={() => this.compareUserData()}
                        />
                    </div>
                </div>
            </div>
        )
    }
}
