import React from 'react';
//import './Authorization.css';
import { toast, ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

const axios = require('axios');
const path = "http://localhost:1234/users";

export default class Authorization extends React.Component {
    state = {
        email: "",
        pwd: "",
        id: "",
        statusLog: "in"
    }

    componentDidMount() {
        const usersArray = axios.get(`${path}`);
        console.log("users: ", usersArray)
    }

    notifyError(err) {
        toast.error(`${err}`, {
            position: "top-center",
            autoClose: 2000,
            hideProgressBar: true,
            closeOnClick: false,
            pauseOnHover: true,
            draggable: true
        });
    }

    notifySuccess(err) {
        toast.success("User added ", {
            position: "top-center",
            autoClose: 2000,
            hideProgressBar: true,
            closeOnClick: false,
            pauseOnHover: true,
            draggable: true
        });
    }

    readEmailInput = (e) => {
        const { value } = e.target;
        this.setState({ email: value });
    }

    readPwdInput = (e) => {
        const { value } = e.target;
        this.setState({ pwd: value });
    }

    compareUserData() {
        let user = {
            email: this.state.email,
            pwd: this.state.pwd,
            status: "in"
        }
        axios.post(`${path}/create`, user)
            .then(() => {
                const { data } = axios.get(`${path}`, user);
                console.log("currentUser auth >> ", data);
                this.props.changeStateApp(data);
            })
            .catch(() => {
                this.notify(`Error, no such user (${this.state.email}) exists!`);
            })
    }

    changeStatusLog() {
        if (this.state.statusLog === "in") {
            this.setState({ statusLog: "up" });
        } else {
            this.setState({ statusLog: "in" });
        }
        this.setState({ email: "" });
        this.setState({ pwd: "" });
    }

    regNewUser() {
        let user = {
            email: this.state.email,
            pwd: this.state.pwd,
            status: "up"
        }
        axios.post(`${path}/create`, user)
            .then(() => {
                this.props.changeStateApp();
            })
            .catch(() => {
                this.notify(`Error, this email (${this.state.email}) is already taken!`);
            })
    }

    render() {
        return (
            <div className="Authorization">
                <ToastContainer />
                <header className="Authorization-header">
                    {this.state.statusLog === "in" ? "Authorization" : "Registration"}
                </header>
                <div className="creds">
                    <div className="email-block">
                        <p className="email-p">Email :</p>
                        <input
                            type="text"
                            value={this.state.email}
                            className="email-value"
                            onChange={(e) => this.readEmailInput(e)}
                        />
                    </div>
                    <div className="pwd-block">
                        <p className="pwd-p">Password :</p>
                        <input
                            type="password"
                            value={this.state.pwd}
                            className="pwd-value"
                            onChange={(e) => this.readPwdInput(e)}
                        />
                    </div>
                    <div className="but-block">
                        {this.state.statusLog === "in" &&
                            <input
                                type="submit"
                                value="Sign in"
                                className="login"
                                onClick={() => this.compareUserData()}
                            />
                        }
                        {this.state.statusLog === "in" &&
                            <p className="offerToReg">
                                If you don't have an account, register, please.
                            </p>
                        }
                        <input
                            type="submit"
                            className="register"
                            value="Register"
                            onClick={this.state.statusLog === "in" ? () => this.changeStatusLog() : () => this.regNewUser()}
                        />
                        {this.state.statusLog === "up" && <input
                            type="submit"
                            value="Cancel"
                            className="cancel"
                            onClick={() => this.changeStatusLog()}
                        />}
                    </div>
                </div>
            </div>
        )
    }
}
